using Microsoft.Xna.Framework;
using Microsoft.Xna.Framework.Graphics;
using System;
using System.Collections.Generic;
using System.IO;

namespace DuckGame
{
    public class NetGraph
    {
        private Queue<NCPacketBreakdown> _packets = new Queue<NCPacketBreakdown>();
        private BitmapFont _font;
        private Texture2D _blank;
        public SpriteBatch batch;
        private float ping;
        private int fps;
        private int _packetsToDraw = 180;
        private float _widthPerPacket = 2f;
        private float _maxHeightPerPacket = 70f;
        private int _currentMaxSize = 100;
        private const string _biosFontData = "";
        private static BFont _biosFont;
        private bool _drawInitialized;
        private static int numFrames;
        private static int numBytes;
        private static float avgBytes;
        private static int numHeaderBytes;
        private static int numGhostBytes;
        private static int numAckBytes;
        private static float avgHeaderBytes;
        private static float avgGhostBytes;
        private static float avgAckBytes;

        public NetGraph() => _font = new BitmapFont("biosFont", 8);

        public void LogPacket(NCPacketBreakdown packet)
        {
            _packets.Enqueue(packet);
            if (_packets.Count > 25000)
            {
                for (int index = 0; index < 20000; ++index)
                    _packets.Dequeue();
            }
            ping = Network.ping;
            fps = Graphics.fps;
        }

        public void Update()
        {
        }

        public void Draw() => DevConsole.UpdateGraph(NetworkDebugger.currentIndex, this);

        public void DrawLine(Vec2 p1, Vec2 p2, Color col, float width = 1f)
        {
            ++Graphics.currentDrawIndex;
            p1 = new Vec2(p1.x, p1.y);
            p2 = new Vec2(p2.x, p2.y);
            float rotation = (float)Math.Atan2(p2.y - p1.y, p2.x - p1.x);
            float length = (p1 - p2).length;
            batch.Draw(_blank, (Vector2)p1, new Microsoft.Xna.Framework.Rectangle?(), (Microsoft.Xna.Framework.Color)col, rotation, (Vector2)new Vec2(0f, 0.5f), (Vector2)new Vec2(length, width), SpriteEffects.None, 1f);
        }

        public void InitializeDraw(GraphicsDevice device)
        {
            _drawInitialized = true;
            byte[] buffer = Convert.FromBase64String("");
            _biosFont = new BFont(Texture2D.FromStream(device, new MemoryStream(buffer)), 8);
            if (_blank != null)
                return;
            _blank = new Texture2D(device, 1, 1, false, SurfaceFormat.Color);
            _blank.SetData(new Color[1]
            {
        Color.White
            });
        }

        public void DoDraw(GraphicsDevice device)
        {
            if (!_drawInitialized)
                InitializeDraw(device);
            Vec2 p1_1 = new Vec2(8f, 10f);
            while (_packets.Count > _packetsToDraw)
                _packets.Dequeue();
            int num1 = 0;
            int num2 = 0;
            DrawLine(p1_1, new Vec2(p1_1.x + _packetsToDraw * _widthPerPacket, p1_1.y), Color.White);
            int num3 = 0;
            foreach (NCPacketBreakdown packet in _packets)
            {
                int num4 = 0;
                Vec2 vec2 = new Vec2(p1_1.x + num1 * _widthPerPacket, p1_1.y + _maxHeightPerPacket);
                foreach (NCPacketDataType dataType in NCPacketBreakdown.dataTypes)
                {
                    int num5 = packet.Get(dataType);
                    if (num5 > 0)
                    {
                        Color typeColor = NCPacketBreakdown.GetTypeColor(dataType);
                        Vec2 p1_2 = vec2 - new Vec2(0f, num4 / (float)_currentMaxSize * _maxHeightPerPacket);
                        Vec2 p2 = p1_2 - new Vec2(0f, num5 / (float)_currentMaxSize * _maxHeightPerPacket);
                        DrawLine(p1_2, p2, typeColor, _widthPerPacket);
                        num4 += num5;
                        num3 += num5;
                    }
                }
                if (num4 > num2)
                    num2 = num4;
                ++num1;
            }
            if (num2 >= _currentMaxSize)
                _currentMaxSize = num2;
            else
                _currentMaxSize -= 2;
            _biosFont.Draw(batch, "in: " + (_currentMaxSize / 8).ToString() + " bytes - " + ((int)(num3 / _packets.Count / 8)).ToString() + " avg", p1_1.x, (float)(p1_1.y - 9), Color.White);
            _biosFont.Draw(batch, "fps: " + fps.ToString() + "    ping: " + ((int)(ping * 1000)).ToString() + "ms", p1_1.x, (float)(p1_1.y + _maxHeightPerPacket + 8), Color.White);
        }

        public void DrawChart(Vec2 pos)
        {
            if (!(Network.activeNetwork.core is NCBasic))
                return;
            if (!_drawInitialized)
                InitializeDraw(Graphics.device);
            while (_packets.Count > _packetsToDraw)
                _packets.Dequeue();
            int num1 = 0;
            int num2 = 0;
            Graphics.DrawLine(pos, new Vec2(pos.x + _packetsToDraw * _widthPerPacket, pos.y), Color.White, depth: ((Depth)0.9f));
            int num3 = 0;
            foreach (NCPacketBreakdown packet in _packets)
            {
                int num4 = 0;
                Vec2 vec2 = new Vec2(pos.x + num1 * _widthPerPacket, pos.y + _maxHeightPerPacket);
                foreach (NCPacketDataType dataType in NCPacketBreakdown.dataTypes)
                {
                    int num5 = packet.Get(dataType);
                    if (num5 > 0)
                    {
                        Color typeColor = NCPacketBreakdown.GetTypeColor(dataType);
                        Vec2 p1 = vec2 - new Vec2(0f, num4 / (float)_currentMaxSize * _maxHeightPerPacket);
                        Graphics.DrawLine(p1, p1 - new Vec2(0f, num5 / (float)_currentMaxSize * _maxHeightPerPacket), typeColor, _widthPerPacket, (Depth)0.9f);
                        num4 += num5;
                        num3 += num5;
                    }
                }
                if (num4 > num2)
                    num2 = num4;
                ++num1;
            }
            if (num2 >= _currentMaxSize)
                _currentMaxSize = num2;
            else
                _currentMaxSize -= 14;
            ++numFrames;
            numBytes += (Network.activeNetwork.core as NCBasic).bytesThisFrame;
            numHeaderBytes += (Network.activeNetwork.core as NCBasic).headerBytes;
            numGhostBytes += (Network.activeNetwork.core as NCBasic).ghostBytes;
            numAckBytes += (Network.activeNetwork.core as NCBasic).ackBytes;
            if (numFrames >= 60)
            {
                avgBytes = numBytes / (float)numFrames;
                avgHeaderBytes = numHeaderBytes / (float)numFrames;
                avgGhostBytes = numGhostBytes / (float)numFrames;
                avgAckBytes = numAckBytes / (float)numFrames;
                numBytes = 0;
                numHeaderBytes = 0;
                numGhostBytes = 0;
                numAckBytes = 0;
                numFrames = 0;
            }
            Graphics.DrawString("in: " + (_currentMaxSize / 8).ToString() + " bytes - " + ((int)(num3 / _packets.Count / 8)).ToString() + " avg", new Vec2(pos.x, pos.y - 9f), Color.White, (Depth)0.9f);
            Graphics.DrawString("fps: " + fps.ToString() + "    ping: " + ((int)(ping * 1000)).ToString() + "ms     bytes out:" + avgBytes.ToString("0.00") + " hdr:" + avgHeaderBytes.ToString("0.00") + " ack:" + avgAckBytes.ToString("0.00") + " gst:" + avgGhostBytes.ToString("0.00"), new Vec2(pos.x, (float)(pos.y + _maxHeightPerPacket + 8f)), Color.White, (Depth)0.9f);
        }

        public void PreUpdate()
        {
            if (!(Network.activeNetwork.core is NCBasic))
                return;
            (Network.activeNetwork.core as NCBasic).bytesThisFrame = 0;
            (Network.activeNetwork.core as NCBasic).headerBytes = 0;
            (Network.activeNetwork.core as NCBasic).ackBytes = 0;
            (Network.activeNetwork.core as NCBasic).ghostBytes = 0;
        }

