            private static int[] _characterMap = new int[byte.MaxValue];
            private const int kTilesPerRow = 16;
            private int _tileSize = 8;

            public BFont(Texture2D image, int size)
            {
                _texture = image;
                _tileSize = size;
                if (_mapInitialized)
                    return;
                for (int index1 = 0; index1 < byte.MaxValue; ++index1)
                {
                    char ch = (char)index1;
                    _characterMap[index1] = 91;
                    for (int index2 = 0; index2 < _characters.Length; ++index2)
                    {
                        if (_characters[index2] == ch)
                        {
                            _characterMap[index1] = index2;
                            break;
                        }
                    }
                }
                _mapInitialized = true;
            }

            public void Draw(SpriteBatch batch, string text, float xpos, float ypos, Color c)
            {
                float num1 = 0f;
                float num2 = 0f;
                for (int index = 0; index < text.Length; ++index)
                {
                    if (text[index] == '\n')
                    {
                        num1 += _tileSize * 8;
                        num2 = 0f;
                    }
                    else
                    {
                        int character = _characterMap[text[index]];
                        int x = character % 16 * _tileSize;
                        int y = character / 16 * _tileSize;
                        batch.Draw(_texture, (Vector2)new Vec2(xpos + num2, ypos), new Microsoft.Xna.Framework.Rectangle?((Microsoft.Xna.Framework.Rectangle)new Rectangle(x, y, _tileSize, _tileSize)), (Microsoft.Xna.Framework.Color)c, 0f, (Vector2)Vec2.Zero, (Vector2)new Vec2(1f, 1f), SpriteEffects.None, 1f);
                        num2 += _tileSize;
                    }
                }
            }
        }
